import React from 'react';
import { useEffect, useState } from 'react';
import { SafeAreaView, View, Text, StyleSheet, Dimensions, TouchableOpacity, Button, Image } from 'react-native';
import Ionicons from 'react-native-vector-icons/Ionicons';
import { Audio } from 'expo-av';



const { width, height } = Dimensions.get("window");

const MusicPlayer = () => {

    const [sound, setSound] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);

  useEffect(() => {
    const loadSound = async () => {
      const { sound } = await Audio.Sound.createAsync(
        { uri: 'https://canada.startradio.in:8010/radio.mp3' },
        { shouldPlay: true }
      );
      setSound(sound);
    };

    loadSound();
  }, []);

  const play = async () => {
    if (sound!== null) {
      await sound.playAsync();
      setIsPlaying(true);
    }
  };

  const pause = async () => {
    if (sound!== null) {
      await sound.pauseAsync();
      setIsPlaying(false);
    }
  };


    return (
        <SafeAreaView style={styles.container}>
        <View style={styles.mainContainer}>
            <View style={styles.artwork}>
                <Image source={require('../assets/logoImage.jpg')} style={styles.artworkImage}/>
            </View> 
        </View>
        <View style={styles.adsContainer}>
        {/* <AdBanner /> */}
        <Text style={{color: '#000', textAlign: 'center'}}>--Advertisement--</Text>
        </View>
        <View style={{
            borderTopColor: "#393E46",
            borderTopWidth: 1,
            width: width,
            alignItems: 'center',
            paddingVertical: 15,
        }}>
            <View style={{flexDirection: 'row', justifyContent: 'space-between', width: '80%'}}></View>
            {isPlaying ? (
            <TouchableOpacity onPress={pause}>
               <Ionicons name="pause-outline" size={80} color="#ffffff" />
            </TouchableOpacity>
            ) : (
                <TouchableOpacity onPress={play}>
               <Ionicons name="play-outline" size={80} color="#ffffff" />
            </TouchableOpacity>
            )}
        </View>

        <View style={styles.footerContainer}>
            
        <TouchableOpacity onPress={()=>{}}>
               <Ionicons name="home-outline" size={30} color="#f3f7d7" />
            </TouchableOpacity>
            <TouchableOpacity onPress={()=>{}}>
               <Ionicons name="earth-outline" size={30} color="#f3f7d7" />
            </TouchableOpacity>
            <TouchableOpacity onPress={()=>{}}>
               <Ionicons name="call-outline" size={30} color="#f3f7d7" />
            </TouchableOpacity>
            
        </View>
        
        

        </SafeAreaView>
        
    );

};

export default MusicPlayer;

const styles = StyleSheet.create({
    container: {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: '#222831',

    },
    adsContainer: {
        flexDirection: 'row',
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: '#cfcfcf', 
        width: width,
        padding: 40,
    },
    mainContainer: {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center'
    },
    footerContainer: {
        flexDirection: 'row',
        borderTopColor: "#393E46",
        borderTopWidth: 1,
        width: width,
        paddingVertical: 15,
        width: '100%',
        justifyContent: 'space-between',
        padding: 20,
        
    },
    artworkImage: {
        borderRadius: 250,
    },
});
